import 'package:day_night_switcher/day_night_switcher.dart';
import 'package:flutter/material.dart';
import 'package:moviles1/provider/theme_provider.dart';
import 'package:moviles1/settings/styles_settings.dart';
import 'package:provider/provider.dart';

class DashboradScreen extends StatefulWidget {
  DashboradScreen({super.key});

  @override
  State<DashboradScreen> createState() => _DashboradScreenState();
}

class _DashboradScreenState extends State<DashboradScreen> {
  bool isDarkModeEnabled = false;

  @override
  Widget build(BuildContext context) {
    ThemeProvider theme = Provider.of<ThemeProvider>(context);

    return Scaffold(
      appBar: AppBar(
        title: Text('Socialitec'),
      ),
      drawer: Drawer(
        child: ListView(children: [
          const UserAccountsDrawerHeader(
              currentAccountPicture: CircleAvatar(
                backgroundImage: NetworkImage(
                    'data:image/jpeg;base64,'),
              ),
              accountName: Text('Adrian'),
              accountEmail: Text('adrianmorales927@gmail.com')),
          ListTile(
            onTap: () {},
            title: Text('Practica 1'),
            subtitle: Text('descripcion de la Practica'),
            leading: Icon(Icons.settings),
            trailing: Icon(Icons.chevron_right),
          ),
          DayNightSwitcher(
              isDarkModeEnabled: isDarkModeEnabled,
              onStateChanged: (isDarkModeEnabled) {
                isDarkModeEnabled
                    ? theme.setthemeData(StyleSettings.DarTheme(context))
                    : theme.setthemeData(StyleSettings.lightTheme(context));
                this.isDarkModeEnabled = isDarkModeEnabled;
                setState(() {});
              })
        ]),
      ),
    );
  }
}
